import { Request, Response } from "express";
import UserService from "../services/userService";

class UserController {
  public async createUser(req: Request, res: Response): Promise<void> {
    try {
      const user = await UserService.createUser(req.body);
      res.status(201).json(user);
    } catch (error: any) {
      res.status(400).json({ error: error.message });
    }
  }

  public async login(req: Request, res: Response): Promise<void> {
    try {
      const { email, password } = req.body;
      const { id, name, role, token } = await UserService.authenticateUser(
        email,
        password
      );
      res.status(200).json({ id, name, role, token });
    } catch (error: any) {
      res.status(400).json({ error: error.message });
    }
  }

  public async getUserById(req: Request, res: Response): Promise<void> {
    const userId = req.params.id;

    // if (!userId.match(/^[0-9a-fA-F-]{36}$/)) {
    //   console.log("Invalid user ID format");
    // }
    const uuidRegex =
      /^[0-9a-fA-F]{8}-[0-9a-fA-F]{4}-[0-9a-fA-F]{4}-[0-9a-fA-F]{4}-[0-9a-fA-F]{12}$/;
    if (!uuidRegex.test(userId)) {
      console.log("Invalid user ID format");
    }
    try {
      const user = await UserService.getUserById(userId);
      if (user) {
        res.status(200).json(user);
      } else {
        res.status(404).json({ message: "User not found" });
      }
    } catch (error: any) {
      res.status(400).json({ error: error.message });
    }
  }

  public async getAllUsers(req: Request, res: Response): Promise<void> {
    try {
      const users = await UserService.getAllUsers();
      res.status(200).json(users);
    } catch (error: any) {
      res.status(400).json({ error: error.message });
    }
  }

  public async updateUser(req: Request, res: Response) {
    const userId = req.params.id;
    const data = req.body;

    try {
      const updatedUser = await UserService.updateUser(userId, data);
      res.json(updatedUser);
    } catch (error: any) {
      res.status(500).json({ error: error.message });
    }
  }

  public async deleteUser(req: Request, res: Response) {
    try {
      const userId = req.params.id;
      const deleted = await UserService.deleteUser(userId);
      if (deleted === 0) {
        return res.status(404).json({ message: "User not found" });
      }
      res.status(204).send();
    } catch (error) {
      if (error instanceof Error) {
        if (error.message === "Invalid UUID format") {
          res.status(400).json({ error: error.message });
        } else {
          res.status(500).json({ error: "Internal Server Error" });
        }
      } else {
        res.status(500).json({ error: "Unknown Error" });
      }
    }
  }
}

export default new UserController();

import User from "../models/User";
import bcrypt from "bcryptjs";
import jwt from "jsonwebtoken";
import { validate as validateUUID } from "uuid";

interface UserData {
  name: string;
  email: string;
  password: string;
  role?: "user" | "admin";
}

class UserService {
  public static async createUser(data: UserData) {
    if (!data.email || !data.password || !data.name) {
      throw new Error("All fields are required");
    }

    const existingUser = await User.findOne({ where: { email: data.email } });
    if (existingUser) {
      throw new Error("Email already exists");
    }

    const hashedPassword = await bcrypt.hash(data.password, 10);
    const user = await User.create({ ...data, password: hashedPassword });
    return { id: user.id, name: user.name, email: user.email, role: user.role };
  }

  public static async getUserById(id: string) {
    const user = await User.findByPk(id);
    if (!user) {
      throw new Error("User not found");
    }
    return user;
  }

  public static async getAllUsers() {
    return User.findAll();
  }

  public static async updateUser(id: string, data: Partial<User>) {
    // Check if the user exists
    const user = await User.findByPk(id);
    if (!user) {
      console.log("User not found");
      throw new Error("User not found");
    }

    // Update the user
    const [updatedCount, updatedUsers] = await User.update(data, {
      where: { id },
      returning: true,
    });

    if (updatedCount === 0) {
      console.log("Update failed");
      throw new Error("Update failed");
    }

    return updatedUsers[0];
  }

  static async deleteUser(userId: string): Promise<number> {
    if (!validateUUID(userId)) {
      throw new Error("Invalid UUID format");
    }
    const user = await User.findByPk(userId);
    if (!user) {
      return 0; // No user found to delete
    }
    await user.destroy();
    return 1; // Return 1 to indicate one user was deleted
  }

  public static async authenticateUser(email: string, password: string) {
    const user = await User.findOne({ where: { email } });
    if (!user) throw new Error("User not found");

    const isPasswordValid = await bcrypt.compare(password, user.password);
    if (!isPasswordValid) throw new Error("Invalid password");

    const token = jwt.sign(
      { id: user.id, role: user.role },
      process.env.JWT_SECRET as string,
      { expiresIn: "1h" }
    );
    return {
      id: user.id,
      name: user.name,
      email: user.email,
      role: user.role,
      token,
    };
  }
}

export default UserService;

import { DataTypes, Model, Optional } from "sequelize";
import sequelize from "../config/database";
import Workout from "./WorkoutPlan";
import Progress from "./Progress";
import Notification from "./Notification";

export interface UserAttributes {
  id: string;
  name: string;
  email: string;
  password: string;
  role?: string;
  createdAt?: Date;
  updatedAt?: Date;
}

interface UserCreationAttributes extends Optional<UserAttributes, "id"> {}

class User
  extends Model<UserAttributes, UserCreationAttributes>
  implements UserAttributes
{
  public id!: string;
  public name!: string;
  public email!: string;
  public password!: string;
  public role!: string;
  public createdAt!: Date;
  public updatedAt!: Date;

  static associate() {
    User.hasMany(Workout, { foreignKey: "userId", as: "workouts" });
    User.hasMany(Progress, { foreignKey: "userId", as: "progress" });
    User.hasMany(Notification, { foreignKey: "userId", as: "notifications" });
  }
}

User.init(
  {
    id: {
      type: DataTypes.UUID,
      defaultValue: DataTypes.UUIDV4,
      primaryKey: true,
    },
    name: {
      type: DataTypes.STRING(255),
      allowNull: false,
    },
    email: {
      type: DataTypes.STRING(255),
      allowNull: false,
      unique: true,
      validate: {
        isEmail: {
          msg: "Must be a valid email address",
        },
      },
    },
    password: {
      type: DataTypes.STRING(255),
      allowNull: false,
    },
    role: {
      type: DataTypes.STRING(50),
      defaultValue: "user",
    },
    createdAt: {
      type: DataTypes.DATE,
      defaultValue: DataTypes.NOW,
    },
    updatedAt: {
      type: DataTypes.DATE,
      defaultValue: DataTypes.NOW,
    },
  },
  {
    sequelize,
    tableName: "users",
  }
);

export default User;

