export default class WorkoutController {
  static async createWorkout(req: Request, res: Response) {
    try {
      const { date, duration, userId, workoutPlanId } = req.body;
      const workout = await WorkoutService.createWorkout({
        date,
        duration,
        userId,
        workoutPlanId,
      });
      return res.status(201).json(workout);
    } catch (error) {
      console.error("Create Workout Error:", error); // Log the exact error
      if (error instanceof Error) {
        res.status(400).json({ error: error.message });
      }
    }
  }

  static async getAllWorkouts(req: Request, res: Response) {
    try {
      const workouts = await WorkoutService.getAllWorkouts();
      res.status(200).json(workouts);
    } catch (error) {
      if (error instanceof Error) {
        res.status(400).json({ error: error.message });
      }
      console.log(error);
    }
  }

  static async getWorkoutById(req: Request, res: Response) {
    try {
      const workout = await WorkoutService.getWorkoutById(req.params.id);
      if (!workout) {
        return res.status(404).json({ message: "Workout not found" });
      }
      res.status(200).json(workout);
    } catch (error) {}
  }

  static async updateWorkout(req: Request, res: Response) {
    try {
      const { id } = req.params;
      const { date, duration, userId, workoutPlanId } = req.body;
      const workout = await WorkoutService.updateWorkout(id, {
        date,
        duration,
        userId,
        workoutPlanId,
      });
      if (!workout) {
        return res.status(404).json({ error: "Workout not found" });
      }
      return res.status(200).json(workout);
    } catch (error) {
      console.error("Update Workout Error:", error);
      if (error instanceof Error) {
        res.status(400).json({ error: error.message });
      }
    }
  }

  static async deleteWorkout(req: Request, res: Response) {
    try {
      const deleted = await WorkoutService.deleteWorkout(req.params.id);
      if (!deleted) {
        return res.status(404).json({ message: "Workout not found" });
      }
      res.status(204).json();
    } catch (error) {
      if (error instanceof Error) {
        res.status(400).json({ error: error.message });
      }
      console.log(error);
    }
  }
