import { Request, Response } from "express";
import UserService from "../services/userService";
import { validate as validateUUID } from "uuid";

class UserController {
  public async createUser(req: Request, res: Response): Promise<void> {
    try {
      if (!req.body.email || !req.body.password || !req.body.name) {
        res.status(400).json({ message: "All fields are required" });
      }

      const user = await UserService.createUser(req.body);

      res.cookie("token", user.token, {
        httpOnly: true,
        secure: process.env.NODE_ENV === "production",
        sameSite: "strict",
      });

      const { token, ...userData } = user;

      res.status(201).json(userData);
    } catch (error: any) {
      if (error instanceof Error) {
        res.status(400).json({ message: error.message });
      }
      console.error(error);
    }
  }

  public async login(req: Request, res: Response): Promise<void> {
    try {
      const { email, password } = req.body;

      const { id, name, role, token } = await UserService.authenticateUser(
        email,
        password
      );

      res.cookie("token", token, {
        httpOnly: true,
        secure: process.env.NODE_ENV === "production",
        sameSite: "strict",
      });

      res.status(200).json({ id, name, role });
    } catch (error: any) {
      if (error instanceof Error) {
        res.status(401).json({ message: error.message });
      }
      console.error(error);
    }
  }

  public async logout(req: Request, res: Response): Promise<void> {
    try {
      res.cookie("token", "", {
        httpOnly: true,
        secure: process.env.NODE_ENV === "production",
        sameSite: "strict",
        expires: new Date(0),
      });

      res.status(200).json({ message: "Logout successful" });
    } catch (error: any) {
      if (error instanceof Error) {
        res.status(400).json({ message: error.message });
      }
      console.error(error);
    }
  }

  public async getUserById(req: Request, res: Response): Promise<void> {
    const userId = req.params.id;

    if (!validateUUID(userId)) {
      res.status(400).json({ message: "Invalid UUID format" });
    }
    try {
      const user = await UserService.getUserById(userId);

      if (!user) {
        res.status(404).json({ message: "User not found" });
      }

      res.status(200).json(user);
    } catch (error: any) {
      if (error instanceof Error) {
        res.status(400).json({ message: error.message });
      }
      console.error(error);
    }
  }

  public async getAllUsers(req: Request, res: Response): Promise<void> {
    try {
      const users = await UserService.getAllUsers();
      res.status(200).json(users);
    } catch (error: any) {
      if (error instanceof Error) {
        res.status(400).json({ message: error.message });
      }
      console.error(error);
    }
  }

  public async updateUser(req: Request, res: Response): Promise<void> {
    const data = req.body;

    if (!validateUUID(data.id)) {
      res.status(400).json({ message: "Invalid UUID format" });
      return;
    }

    try {
      const updatedUser = await UserService.updateUser(data.id, data);
      res.json(updatedUser);
    } catch (error: any) {
      if (error instanceof Error) {
        res.status(400).json({ message: error.message });
      }
      console.error(error);
    }
  }

  public async deleteUser(req: Request, res: Response): Promise<void> {
    const { id } = req.params;
    if (!validateUUID(id)) {
      res.status(400).json({ message: "Invalid UUID format" });
      return;
    }
    try {
      const deleted = await UserService.deleteUser(id);
      if (deleted === 0) {
        res.status(404).json({ message: "User not found" });
        return;
      }

      if (res) {
        res.clearCookie("token");
      }

      res.status(204).json();
    } catch (error: any) {
      if (error instanceof Error) {
        console.error("Delete user error:", error.message, error.stack); // Added logging
        res.status(400).json({ message: error.message });
      }
      console.error(error);
    }
  }
}

export default new UserController();

import User, { UserCreationAttributes } from "../models/User";
import bcrypt from "bcryptjs";
import jwt from "jsonwebtoken";
import { validate as validateUUID } from "uuid";
import { Exercise, Workout, sequelize } from "../models/index";

class UserService {
  async createUser(data: UserCreationAttributes) {
    if (!data.email || !data.password || !data.name) {
      throw new Error("All fields are required");
    }

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(data.email)) {
      throw new Error("Invalid email format");
    }

    const passwordRegex = /^(?=.*[A-Za-z]).{8,}$/;
    if (!passwordRegex.test(data.password)) {
      throw new Error(
        "Password must be at least 8 characters long and include at least one letter"
      );
    }

    const nameRegex = /^[a-zA-Z ]{2,30}$/;
    if (!nameRegex.test(data.name)) {
      throw new Error(
        "Name must be between 2 and 30 characters and contain only letters and spaces"
      );
    }

    const existingUser = await User.findOne({ where: { email: data.email } });

    if (existingUser) {
      throw new Error("Email already exists");
    }

    const hashedPassword = await bcrypt.hash(data.password, 10);
    const user = await User.create({
      ...data,
      password: hashedPassword,
    });
    const token = jwt.sign(
      { id: user.id, email: user.email, name: user.name, role: user.role },
      process.env.JWT_SECRET as string,
      { expiresIn: "1h" }
    );

    return {
      id: user.id,
      name: user.name,
      email: user.email,
      role: user.role,
      token,
    };
  }

  async getUserById(id: string) {

    if (!validateUUID(id)) {
      throw new Error("Invalid UUID format");
    }

    const user = await User.findByPk(id);

    const exercisesCount = await Exercise.count({
      where: { userId: id },
    });

    const workoutCount = await Workout.count({
      where: { userId: id },
    });

    if (!user) {
      throw new Error("User not found");
    }

    return {
      id,
      name: user.name,
      email: user.email,
      exercisesCount,
      workoutCount,
      createdAt: user.createdAt,
      updatedAt: user.updatedAt,
    };
  }

  async getAllUsers() {
    return User.findAll();
  }

  async updateUser(id: string, data: any) {
    if (!validateUUID(id)) {
      throw new Error("Invalid UUID format");
    }

    const user = await User.findByPk(id);

    if (!user) {
      throw new Error("User not found");
    }

    if (data.email !== undefined) {
      const isEmailExists = await User.findOne({
        where: { email: data.email },
      });

      if (isEmailExists && isEmailExists.id !== id) {
        throw new Error("Email already exists");
      }

      const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

      if (!emailRegex.test(data.email)) {
        throw new Error("Invalid email format");
      }
    }

    if (data.name !== undefined) {
      const nameRegex = /^[a-zA-Z ]{2,30}$/;

      if (!nameRegex.test(data.name)) {
        throw new Error(
          "Name must be between 2 and 30 characters and contain only letters and spaces"
        );
      }
    }

    return await user.update(data);
  }

  async deleteUser(id: string) {
    if (!validateUUID(id)) {
      throw new Error("Invalid UUID format");
    }
    const transaction = await sequelize.transaction();
    try {
      const user = await User.findByPk(id, { transaction });

      if (!user) {
        await transaction.rollback();
        return 0;
      }

      await Workout.destroy({ where: { userId: id }, transaction });
      await Exercise.destroy({ where: { userId: id }, transaction });

      await user.destroy({ transaction });
      await transaction.commit();

      return 1;
    } catch (error: any) {
      await transaction.rollback();
      console.error(error);
      throw new Error(error.message);
    }
  }

  async authenticateUser(email: string, password: string) {
    if (!email || !password) {
      throw new Error("All fields are required");
    }

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email)) {
      throw new Error("Invalid email format");
    }
    const passwordRegex = /^(?=.*[A-Za-z]).{8,}$/;
    if (!passwordRegex.test(password)) {
      throw new Error(
        "Password must be at least 8 characters long and include at least one letter"
      );
    }

    const user = await User.findOne({ where: { email } });
    if (!user) throw new Error("User not found");

    const isPasswordValid = await bcrypt.compare(password, user.password);
    if (!isPasswordValid) throw new Error("Invalid password");

    const token = jwt.sign(
      { id: user.id, role: user.role },
      process.env.JWT_SECRET as string,
      { expiresIn: "1h" }
    );

    return {
      id: user.id,
      name: user.name,
      email: user.email,
      role: user.role,
      token,
    };
  }
}

export default new UserService();

import { Request, Response, NextFunction } from "express";
import jwt from "jsonwebtoken";

interface CustomRequest extends Request {
  user?: string | jwt.JwtPayload;
}

const authMiddleware = (
  req: CustomRequest,
  res: Response,
  next: NextFunction
) => {
  const cookieToken = req.cookies.token;
  const headerToken = req.header("Authorization")?.replace("Bearer ", "");

  const token = cookieToken || headerToken;

  if (!token) {
    return res.status(401).json({ message: "Token not provided" });
  }

  try {
    const decoded = jwt.verify(token, process.env.JWT_SECRET as string);
    req.user = decoded;
    next();
  } catch (err) {
    return res.status(401).json({ error: "Invalid token" });
  }
};

export default authMiddleware;

